settings.custom_liquid:
{% case some_variable %}
{% when 'add_product' %} 
    {% if product.metafields.custom.additional_product.value %}
    <div>
      <h3>{{ block.settings.heading_txt }}</h3>
    </div>
    {% for additional_pro in product.metafields.custom.additional_product.value %} 
    {% assign add_gid = additional_pro.ref_product %} 
    {% assign gid_ref_product = all_products[add_gid] %}

    <div class="main_cart">
      <div class="parent_cart">
        {% if gid_ref_product.featured_image %}

        <div class="ref_image">
          <img
            height="auto"
            width="50px"
            src="{{ gid_ref_product.featured_image | image_url: width: 246 }}"
            alt=""
          />
        </div>
        {% endif %}
        <div class="ref_name">
          <strong>
            <p>{{ gid_ref_product.title }}</p>

            <div class="price-block">
              <strong>
                {% if gid_ref_product.compare_at_price %}
                <span class="">
                  <s>{{ gid_ref_product.compare_at_price | money }}</s>
                </span>
                <br />
                {% endif %} 
                {% if gid_ref_product.variants.size>1 %}
                  <span style="color: green" class="_price_data">{{ gid_ref_product.price | money }}</span>
                {% else %}
                  <span style="color: green" class="_price_data">{{ gid_ref_product.price | money }}</span>
                {% endif %}
              </strong>
            </div>


            {% if gid_ref_product.variants.size>1 %}
            <select id="_input_selectBox" class="_input_selectBox">
              <option name="pro_id" value="-1">Select Option</option>

              {% for product_varaint in gid_ref_product.variants %}
              <option
                price_data="{{ product_varaint.price | money}}"
                name="pro_id"
                value="{{ product_varaint.id }}"
              >
                {{ product_varaint.title }}
              </option>

              {% endfor %}
            </select>
            {% else %}
            <div class="_single_var">
              <button id="_is_selected_true">Yes</button>
              <button id="_is_selected_false">No</button>
              <input
                id="_hid_checkbox"
                type="checkbox"
                value="{{ gid_ref_product.variants.first.id }}"
              />
            </div>
            <script>
              $(document).ready(function () {
                $("#_is_selected_true").click(function () {
                  $("#_hid_checkbox").prop("checked", true);
                  $(this).addClass("active");
                  $("#_is_selected_false").removeClass("active");
                });

                $("#_is_selected_false").click(function () {
                  $("#_hid_checkbox").prop("checked", false);
                  $(this).addClass("active");
                  $("#_is_selected_true").removeClass("active");
                });
              });
            </script>
            {% endif %}
          </strong>
        </div>

      </div>
    </div>
    {% endfor %}
    <script>
      $(document).ready(function () {
        $(".product-form__buttons").on("click", function () {
          var values = $("._input_selectBox option:selected")
            .map(function () {
              if (this.value != -1) {
                return this.value;
              }
            })
            .toArray();

          console.log(values);
          if (values.length != 0) {
            setTimeout(addItemToCart(values), 500);
          }
        });
        $(".product-form__buttons").on("click", function () {
          var chbx_value = $("#_hid_checkbox").val();
          if ($("#_hid_checkbox").is(":checked")) {
            console.log("val", chbx_value);
            setTimeout(function () {
              addItemToCart(chbx_value);
            }, 500);
          }
        });

        $("._input_selectBox").change(function () {
          var _price_cr = $(this).find(":selected").attr("price_data");
          console.log(_price_cr);
          var spanElement = $(this)
            .closest(".parent_cart")
            .find("._price_data");
          spanElement.text(_price_cr);
        });
      });

      function addItemToCart(variant_id, qty, frequency, unit_type) {
        console.log("yes going inside cart...");
        setTimeout(function () {
          data = {
            id: variant_id,
            quantity: 1,
          };

          jQuery.ajax({
            type: "POST",
            url: "/cart/add.js",
            data: data,
            dataType: "json",
            success: function () {
              document.documentElement.dispatchEvent(
                new CustomEvent("cart:refresh", {
                  bubbles: true, //this code is for prestige theme, is to refresh the cart
                })
              );
            },
          });
          document.documentElement.dispatchEvent(
            new CustomEvent("cart:refresh", {
              bubbles: true, // same code for prestige theme
            })
          );
        }, 1000);
      }
    </script>

    <style>
      .main_cart {
        width: auto;
        display: flex;
        justify-content: center;
        max-width: 100%;
        border-radius: 8px;
        border: 1px solid;
      }
      .main_cart .checkbox {
        display: flex;
      }
      .ref_image {
        display: flex;
      }
      .ref_image img {
        border-radius: 4px;
        width: 55px;
      }
      .parent_cart {
        padding-top: 5px;
        padding-bottom: 5px;
        display: flex;
        align-items: center;
        /* justify-content: space-between; */
        width: fit-content;
        border-radius: 8px;
        min-width: 100%;
      }
      .parent_cart div {
        margin: 0px 0px 0px 0px;
        padding: 0px 10px 0px 10px;
      }
      .checkbox strong {
        margin-right: 2px;
      }
      ._single_var {
        display: flex;
        gap: 10px;
        padding: 0 !important;
      }
      #_is_selected_false {
        /* background: white;
        border: 1px solid black;
        color: black; */
      }
      ._single_var .active {
        background: #000;
        color: white;
      }
      ._single_var button {
        background: #fff;
        color: black;
        border: 1px solid black;
        padding: 2px 11px;
        border-radius: 5px;
        cursor: pointer;
      }
      #_hid_checkbox {
        visibility: hidden;
      }
      .price-block {
        padding: 0 !important;
      }
      @media only screen and (max-width: 600px) {
        .ref_name p {
          font-size: 10px;
        }
      }
      .ref_name p {
        font-size: 12px;
        margin: 0;
      }
      .checkbox span {
        font-size: 11px;
      }
      .checkbox {
        display: flex;
      }
      .ref_name {
        width: 100%;
        /* max-width: 42%; */
      }
      ._input_selectBox {
        border: solid 1px #121212;
        width: 100%;
        padding: 5px;
        border-radius: 4px;
        margin-bottom: 0;
        outline: none;
        box-shadow: none;
      }
    </style>
    {% endif %}
  {% endcase %}
